#import AD module
ipmo ActiveDirectory

# * UPDATE THESE LINES WITH NEW USER INFO *
$dept = ""
$display = "Chitra Babu Admin"
$first = "Chitra"
$last = "Babu"
$mobile = ""
$phone = ""
$title = ""
$upn = "cbabu.admin@insmed.com"
$sam = "cbabu.admin"
# / UPDATE THESE LINES WITH NEW USER INFO /

$proxy = "SMTP:"+$upn
$downlevel = "INSMED\"+"$sam"
write "$sam, $dept, $display, $first, $last, $mobile, $phone, $title, $upn"

New-ADUser -Path 'OU=IT Admins,DC=insmed,DC=local' -SamAccountName $sam -name $sam -UserPrincipalName $upn -GivenName $first -Surname $last -DisplayName $display -Title $title -Department $dept -EmailAddress $upn 
Set-ADUser $sam -Add @{proxyAddresses = $proxy}

$pw = Get-Password
Set-ADAccountPassword $sam -NewPassword (ConvertTo-SecureString -AsPlainText "$pw" -Force)

write "-------------------------------------------------------------------------------"
write "DisplayName, SamAccountName, UserPrincipalName, Down-Level Logon Name, Password"
write "$display, $sam, $upn, $downlevel, $pw"
write "-------------------------------------------------------------------------------"
write ""

